import React, { Component } from "react"
import AppBar from "@material-ui/core/AppBar"
import Toolbar from "@material-ui/core/Toolbar"
import Menu from "@material-ui/core/Menu"
import MenuItem from "@material-ui/core/MenuItem"
import IconButton from "@material-ui/core/IconButton"
import Typography from "@material-ui/core/Typography"
import InputBase from "@material-ui/core/InputBase"
import Badge from "@material-ui/core/Badge"
import InstagramIcon from "@material-ui/icons/Instagram"
import SearchIcon from "@material-ui/icons/Search"
import AccountCircle from "@material-ui/icons/AccountCircle"
import MailIcon from "@material-ui/icons/Mail"
import Button from "@material-ui/core/Button"
import NotificationsIcon from "@material-ui/icons/Notifications"
import Card from "@material-ui/core/Card"
import CardHeader from "@material-ui/core/CardHeader"
import CardMedia from "@material-ui/core/CardMedia"
import CardContent from "@material-ui/core/CardContent"
import CardActions from "@material-ui/core/CardActions"
import Avatar from "@material-ui/core/Avatar"
import FavoriteIcon from "@material-ui/icons/Favorite"
import ShareIcon from "@material-ui/icons/Share"
import BookmarkBorderIcon from "@material-ui/icons/BookmarkBorder"
import BookmarkIcon from "@material-ui/icons/Bookmark"
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder"
import { newsFeedList } from "../jsonUtil"
import { withRouter, Link } from "react-router-dom"
import { connect } from "react-redux"
import celebrate from "../assets/celebrate.png"
import celebrateBg from "../assets/celebrateBg.jpg"
import pageUnderConstruction from "../assets/pageUnderConstruction.jpg"
import messagePageConstruction from "../assets/messagePageConstruction.jpg"

import {
  INSTAGRAM,
  LOGOUT,
  MESSAGE_INFO,
  NOTIFICATION_INFO,
  PROFILE,
  PROFILE_INFO,
  SAVED_ITEMS,
} from "../CommonConstants"

import "./Home.css"
import Popover from "../Components/Popover"
import ModalComponent from "../Components/ModalComponent"
import AppHeader from "../Components/AppHeader"

let modalMessage = "",
  modalImage = "",
  modalPage = ""

export class Home extends Component {
  constructor(props) {
    super(props)
    this.state = {
      anchor: false,
      anchorEl: null,
      dialogOpen: false,
      modalOpen: false,
      liked: false,
    }
  }

  handleDialogOpen = () =>
    this.setState({
      dialogOpen: true,
      anchorEl: null,
    })

  openPopOver = (event) =>
    this.setState({
      anchorEl: event.currentTarget,
    })

  popOverClose = () => {
    this.setState({
      anchorEl: null,
    })
    this.state.dialogOpen = false
    this.state.modalOpen = false
  }

  handleLike = (index, item, event) => {
    item.likedStatus = !item.likedStatus
    // Toggle the state variable liked
    this.setState({ liked: !item.likedStatus })
  }

  handleProfile = () => {
    this.setState({ modalOpen: true })
    modalMessage = PROFILE_INFO
    modalImage = celebrate
    modalPage = "profile"
  }

  handleNotificationIcon = () => {
    this.setState({ modalOpen: true })
    modalMessage = NOTIFICATION_INFO
    modalImage = pageUnderConstruction
    modalPage = "notification"
  }

  handleMessageIcon = () => {
    this.setState({ modalOpen: true })
    modalMessage = MESSAGE_INFO
    modalImage = messagePageConstruction
    modalPage = "message"
  }

  handleSavedItems = () => this.props.history.push("/savedItems")

  likeRender = (event, index, item) => {
    return (
      <IconButton
        onClick={this.handleLike.bind(this, index, item)}
        aria-label="add to favorites"
      >
        {item.likedStatus == true ? (
          <>
            <Typography>1 like</Typography>
            <FavoriteIcon style={{ color: "red" }} />
          </>
        ) : (
          <FavoriteBorderIcon />
        )}
      </IconButton>
    )
  }

  updateModalState = (modalOpenFromChild) => {
    this.setState({ modalOpen: modalOpenFromChild })
  }

  handleUpdatePopoverState = (dialogOpenFromChild) => {
    this.setState({ dialogOpen: dialogOpenFromChild })
  }

  render() {
    return (
      <div>
        <AppHeader/>  
        <div className="newsFeedBody">
          {newsFeedList.map((item, index) => {
            return (
              <Card className="root">
                <CardHeader avatar={
                  <Avatar aria-label="recipe" className="avatar">
                    {item.avatar}
                  </Avatar>
                }
                  title={item.title}
                  subheader="September 14, 2016"
                />
                <CardMedia
                  className="media"
                  image={item.image}
                  height
                  title="Paella dish"
                />
                <CardContent>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    {item.description}
                  </Typography>
                </CardContent>
                <CardActions disableSpacing>
                  {this.likeRender(this, index, item)}
                  <IconButton aria-label="share">
                    <ShareIcon />
                  </IconButton>
                  <IconButton aria-label="save">
                    <BookmarkBorderIcon />
                  </IconButton>
                </CardActions>
              </Card>
            )
          })}
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    username: state.common.username,
  }
}

export default withRouter(connect(mapStateToProps, null)(Home))
