import React, { Component } from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import InputBase from "@material-ui/core/InputBase";
import Badge from "@material-ui/core/Badge";
import InstagramIcon from "@material-ui/icons/Instagram";
import SearchIcon from "@material-ui/icons/Search";
import AccountCircle from "@material-ui/icons/AccountCircle";
import MailIcon from "@material-ui/icons/Mail";
import Button from "@material-ui/core/Button";
import NotificationsIcon from "@material-ui/icons/Notifications";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import BookmarkBorderIcon from "@material-ui/icons/BookmarkBorder";
import BookmarkIcon from "@material-ui/icons/Bookmark";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import { newsFeedList } from "../jsonUtil";
import { withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import celebrate from "../assets/celebrate.png";
import celebrateBg from "../assets/celebrateBg.jpg";
import pageUnderConstruction from "../assets/pageUnderConstruction.jpg";
import messagePageConstruction from "../assets/messagePageConstruction.jpg";

import {
  INSTAGRAM,
  LOGOUT,
  MESSAGE_INFO,
  NOTIFICATION_INFO,
  PROFILE,
  PROFILE_INFO,
  SAVED_ITEMS,
} from "../CommonConstants";

import "./Home.css";
import Popover from "../Components/Popover";
import ModalComponent from "../Components/ModalComponent";

let clickedIndex = "",
  modalMessage = "",
  modalImage = "",
  modalPage = "";

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      anchor: false,
      anchorEl: null,
      dialogOpen: false,
      modalOpen: false,
      liked: false,
    };
  }

  handleDialogOpen = () =>
    this.setState({
      dialogOpen: true,
      anchorEl: null,
    });

  openPopOver = (event) =>
    this.setState({
      anchorEl: event.currentTarget,
    });

  popOverClose = () => {
    this.setState({
      anchorEl: null,
    });
    this.state.dialogOpen = false;
    this.state.modalOpen = false;
  };

  handleLike = (index, event) => {
    let localLiked = this.state.liked;
    clickedIndex = index;
    // Toggle the state variable liked
    localLiked = !localLiked;
    this.setState({ liked: localLiked });
  };

  handleProfile = () => {
    this.setState({ modalOpen: true });
    modalMessage = PROFILE_INFO;
    modalImage = celebrate;
    modalPage = "profile";
  };

  handleNotificationIcon = () => {
    this.setState({ modalOpen: true });
    modalMessage = NOTIFICATION_INFO;
    modalImage = pageUnderConstruction;
    modalPage = "notification";
  };

  handleMessageIcon = () => {
    this.setState({ modalOpen: true });
    modalMessage = MESSAGE_INFO;
    modalImage = messagePageConstruction;
    modalPage = "message";
  };

  handleSavedItems = () => this.props.history.push("/savedItems");

  likeRender = (event, index) => {
    return (
      <IconButton
        onClick={this.handleLike.bind(this, index)}
        aria-label="add to favorites"
      >
        {this.state.liked === true && clickedIndex == index ? (
          <>
            <Typography>1 like</Typography>
            <FavoriteIcon style={{ color: "red" }} />
          </>
        ) : (
          <FavoriteBorderIcon />
        )}
      </IconButton>
    );
  };

  handleUpdateModalState=()=>{
    console.log("======modal to home", this.state.modalOpen)
  }

  render() {
    return (
      <div>
        <div className="headerDiv">
          <AppBar position="fixed">
            <Toolbar>
              <IconButton
                edge="start"
                color="inherit"
                aria-label="open drawer"
              >
                <a target="_blank" href="https://about.instagram.com/">
                  <InstagramIcon />
                </a>
              </IconButton>
              <Typography variant="h6" noWrap>
                {INSTAGRAM}
              </Typography>
              <div className="seachField">
                <div className="seachIcon">
                  <SearchIcon />
                </div>
                <InputBase
                  className="seachInput"
                  placeholder="Searchâ€¦"
                  inputProps={{ "aria-label": "search" }}
                />
              </div>
              <div />
              <div className="headerLeftIcons">
                <IconButton
                  aria-label="show 4 new mails"
                  color="inherit"
                  onClick={this.handleMessageIcon}
                >
                  <Badge badgeContent={4} color="secondary">
                    <MailIcon />
                  </Badge>
                </IconButton>
                <IconButton
                  aria-label="show 17 new notifications"
                  color="inherit"
                  onClick={this.handleNotificationIcon}
                >
                  <Badge badgeContent={17} color="secondary">
                    <NotificationsIcon />
                  </Badge>
                </IconButton>
                <Button
                  className="AccountIcon"
                  variant="contained"
                  edge="end"
                  aria-label="account of current user"
                  aria-haspopup="true"
                  color="inherit"
                  onClick={this.openPopOver}
                >
                  <AccountCircle />
                </Button>
                <Menu
                  anchorEl={this.state.anchorEl}
                  keepMounted
                  open={!!this.state.anchorEl}
                  onClose={this.popOverClose}
                  getContentAnchorEl={null}
                  anchorOrigin={{
                    vertical: "bottom",
                    horizontal: "center",
                  }}
                  transformOrigin={{
                    vertical: "top",
                    horizontal: "right",
                  }}
                >
                  <MenuItem onClick={this.handleProfile}>{PROFILE}</MenuItem>
                  <MenuItem onClick={this.handleDialogOpen}>{LOGOUT}</MenuItem>
                  <MenuItem onClick={this.handleSavedItems}>
                    {SAVED_ITEMS}
                  </MenuItem>
                </Menu>
                {this.state.dialogOpen ? (
                  <Popover dialogOpen={this.state.dialogOpen} />
                ) : (
                  ""
                )}
                {this.state.modalOpen ? (
                  <ModalComponent
                    modalOpen={this.state.modalOpen}
                    page={modalPage}
                    message={modalMessage}
                    image={modalImage}
                    updateModalState={this.handleUpdateModalState}
                    bgImage="url('../assets/celebrateBg.jpg')"
                  />
                ) : (
                  ""
                )}
              </div>
            </Toolbar>
          </AppBar>
        </div>
        <div className="newsFeedBody">
          {newsFeedList.map((item, index) => {
            return (
              <Card className="root">
                <CardHeader
                  avatar={
                    <Avatar aria-label="recipe" className="avatar">
                      {item.avatar}
                    </Avatar>
                  }
                  title={item.title}
                  subheader="September 14, 2016"
                />
                <CardMedia
                  className="media"
                  image={item.image}
                  height
                  title="Paella dish"
                />
                <CardContent>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    {item.description}
                  </Typography>
                </CardContent>
                <CardActions disableSpacing>
                  {this.likeRender(this, index)}
                  <IconButton aria-label="share">
                    <ShareIcon />
                  </IconButton>
                  <IconButton aria-label="save">
                    <BookmarkBorderIcon />
                  </IconButton>
                </CardActions>
              </Card>
            );
          })}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    username: state.common.username,
  };
};

export default withRouter(connect(mapStateToProps, null)(Home));
