import { SAVED_LIST, USERNAME } from '../CommonConstants';


const INITIAL_STATE = {
    username: "",
    newsfeedSavedList: []
}

const commonReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {

        case USERNAME:

            return {
                ...state, username: action.payload,

            }
        case SAVED_LIST:
            return {
                ...state, newsfeedSavedList: action.payload
            }

        default: return state

    }

};

export default commonReducer
