import {  USERNAME } from '../CommonConstants';


    const INITIAL_STATE = {
        username:""
    };

    const commonReducer = (state = INITIAL_STATE, action) => {
        switch (action.type) {

               case USERNAME:

                return {
                   ...state, username: action.payload,
 
                };

             default: return state;

        }

    };

    export default commonReducer;
