import React, { Component } from "react";
import { TextField, Button, IconButton } from "@material-ui/core";
import InstagramIcon from "@material-ui/icons/Instagram";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux"

import {
  increaseCounter,
  decreaseCounter,
} from "../action/commonAction"
import "./Login.css"; 
class Login extends Component {

  constructor(props){
    super(props)
    this.state={
      username:""
    }
  }

  // To handle user tries to login
  handleSubmit = () => {
    this.props.history.push("/home");
  };

  render() {
    return (
      <div style={{ marginTop: "160px" }}>
        <IconButton color="inherit" aria-label="open drawer">
          <InstagramIcon className="LoginLogoIcon" />
        </IconButton>
        <br />
        <TextField id="standard-basic" label="Username" />
        <br />
        <TextField id="standard-search" label="Password" type="password" />
        <br />
        <Button
          onClick={this.handleSubmit}
          type="submit"
          style={{ top: "20px" }}
          variant="outlined"
          color="primary"
        >
          Login
        </Button>
        <div style={{marginTop:"25px"}}>
        
        <div>Count: {this.props.count}</div>

        <button onClick={() => this.props.increaseCounter()}>Increase Count</button>

        <button onClick={() => this.props.decreaseCounter()}>Decrease Count</button>
        </div>
      </div>

    );
  }
}

const mapStateToProps = state => {
  return {
    count: state.common.count,
  }
}

const mapDispatchToProps = dispatch => {
  return {
    increaseCounter: () => dispatch(increaseCounter()),

    decreaseCounter: () => dispatch(decreaseCounter()),
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Login));
